mapper.insert:
		INSERT INTO RESERVATION (
			RESERVE_NO
			, RESERVE_DATE
			, RESERVE_PRICE
			, RESERVE_PEOPLE
			, CHECKIN
			, CHECKOUT
			, LODGMENT_NO
			, ROOM_NO
			, MEMBER_NO
			, RESERVE_POINTS
			, DEDUCTION_POINT
		)
		VALUES (
			TO_CHAR(RESV_SEQ.NEXTVAL)
			, SYSDATE
			, #{reservePrice}
			, #{reservePeople}
			, #{checkin}
			, #{checkout}
			, #{lodgmentNo}
			, #{roomNo}
			, #{memberNo}
			, #{reservePoints}
 			, #{deductionPoint}
		)

mapper.select:
SELECT * FROM ( SELECT U.*, ROWNUM RN FROM ( SELECT RESERVE_NO, (SELECT MEMBER_NAME FROM MEMBER WHERE MEMBER.MEMBER_NO = RESERVATION.MEMBER_NO) AS memberName, (SELECT MEMBER_EMAIL FROM MEMBER WHERE MEMBER.MEMBER_NO = RESERVATION.MEMBER_NO) AS memberEmail, ROOM_NO, RESERVE_DATE, RESERVE_PRICE, RESERVE_PEOPLE, CHECKIN, CHECKOUT, RESERVE_STATE FROM RESERVATION
RESERVE_NO IN
#{item}
AND RESERVE_NO LIKE '%' || #{searchKeyword} || '%'
AND ROOM_NO LIKE '%' || #{searchKeyword} || '%'
ORDER BY
#{sort}
RESERVE_NO
) U ) A WHERE RN BETWEEN #{start} and #{end}
