mapper.select:
	SELECT * FROM ( SELECT U.*, ROWNUM RN FROM (
		SELECT R.LODGMENT_NO, L.COMPANY AS "company", R.ROOM_NO, R.ROOM_NAME, R.ROOM_QUANTITY, R.ROOM_PRICE, R.ROOM_CAPA
		FROM ROOM R JOIN LODGMENT L
		ON L.LODGMENT_NO = R.LODGMENT_NO

		R.LODGMENT_NO = #{lodgmentNo}

			ROOM_NO IN

				#{item}



			AND ROOM_NO LIKE '%' || #{searchKeyword} || '%'


			AND ROOM_NAME LIKE '%' || #{searchKeyword} || '%'		


	ORDER BY


			#{sort}


			ROOM_NO


) U ) A WHERE RN BETWEEN #{start} and #{end}

	SELECT * FROM ( SELECT U.*, ROWNUM RN FROM (
		SELECT ROOM_NO, ROOM_NAME, ROOM_QUANTITY, ROOM_PRICE, ROOM_CAPA
		FROM ROOM


			ROOM_NO IN

				#{item}



			AND ROOM_NO LIKE '%' || #{searchKeyword} || '%'


			AND ROOM_NAME LIKE '%' || #{searchKeyword} || '%'		



	ORDER BY


			#{sort}


			ROOM_NO


) U ) A WHERE RN BETWEEN #{start} and #{end}
