SELECT * FROM ( SELECT U.*, ROWNUM RN FROM ( SELECT TOUR_ID ,TOUR_TITLE ,(SELECT MEMBER_NO FROM MEMBER WHERE MEMBER.MEMBER_NO = TOUR.MEMBER_NO) AS memberNo ,(SELECT MEMBER_NAME FROM MEMBER WHERE MEMBER.MEMBER_NO = TOUR.MEMBER_NO) AS memberName ,TOUR_DATE ,TOUR_LOCATION ,TOUR_STATE FROM TOUR
TOUR_ID IN
#{item}
AND TOUR_ID LIKE '%' || #{searchKeyword} || '%'
AND TOUR_LOCATION LIKE '%' || #{searchKeyword} || '%'
ORDER BY
#{sort}
TOUR_ID
) U ) A WHERE RN BETWEEN #{start} and #{end}
