mapper.select:
SELECT * FROM ( SELECT T.*, ROWNUM RN FROM ( SELECT TOUR_ID, TOUR_TITLE, (SELECT MEMBER_NO FROM MEMBER WHERE MEMBER.MEMBER_NO = TOUR.MEMBER_NO) AS memberNo, (SELECT MEMBER_NAME FROM MEMBER WHERE MEMBER.MEMBER_NO = TOUR.MEMBER_NO) AS memberName, TOUR_DATE, TOUR_LOCATION, TOUR_STATE FROM TOUR
TOUR_ID > #{tourId}
ORDER BY TOUR_DATE ) T ) A
WHERE RN <= #{pageUnit}
WHERE RN <= 8
